def get_edit_operations(v0: str, v1: str) -> list:
    v2, v3 = (len(v0), len(v1))
    v4 = [[0] * (v3 + 1) for _ in range(v2 + 1)]
    for v5 in range(v2 + 1):
        v4[v5][0] = v5
    for v6 in range(v3 + 1):
        v4[0][v6] = v6
    for v5 in range(1, v2 + 1):
        for v6 in range(1, v3 + 1):
            if v0[v5 - 1] == v1[v6 - 1]:
                v4[v5][v6] = v4[v5 - 1][v6 - 1]
            else:
                v4[v5][v6] = 1 + min(v4[v5 - 1][v6], v4[v5][v6 - 1], v4[v5 - 1][v6 - 1])
    v7 = []
    v5, v6 = (v2, v3)
    while v5 > 0 or v6 > 0:
        if v5 > 0 and v6 > 0 and (v0[v5 - 1] == v1[v6 - 1]):
            v5 -= 1
            v6 -= 1
        elif v6 > 0 and (v5 == 0 or (v4[v5][v6 - 1] <= v4[v5 - 1][v6] and v4[v5][v6 - 1] <= v4[v5 - 1][v6 - 1])):
            v7.append({'op': 'insert', 'char': v1[v6 - 1], 'pos': v5})
            v6 -= 1
        elif v5 > 0 and (v6 == 0 or (v4[v5 - 1][v6] <= v4[v5][v6 - 1] and v4[v5 - 1][v6] <= v4[v5 - 1][v6 - 1])):
            v7.append({'op': 'delete', 'char': v0[v5 - 1], 'pos': v5 - 1})
            v5 -= 1
        else:
            v7.append({'op': 'replace', 'from': v0[v5 - 1], 'to': v1[v6 - 1], 'pos': v5 - 1})
            v5 -= 1
            v6 -= 1
    return list(reversed(v7))
